import streamlit as st 

st.title("Email Spam Classiification app")
st.subheader("Done with streamlit")
st.text("Welcome to your first interactive app")
st.write("choose your email to classify spam and ham")

chai = st.selectbox("your favourite chai : ", ["Masala chai", "Lemon Tea", "Adrag chai", "Kesari chai"])
st.write(f"You choose {chai} which is excellent")

st.success(f"Yor {chai} has been brewed")

if st.button('Make Chai'):
    st.success("Your chai is being brewed")

add_masala = st.checkbox("Add Masala")

if add_masala:
    st.write("Masala added to your chai")

tea_type = st.radio("your type are : ", ["Milk", "Water", "Honey"])
st.write(f"selected base {tea_type}")

flavour = st.selectbox("Choose flavour: ", ["Adrak", "kesar", "Tulsi"])
st.write(f"selected your base {flavour}")

sugar = st.slider("sugar level", 0, 5, 2)
st.write(f"you selected sugar level : {sugar}")

cups = st.number_input("How many cups ?", min_value=1, max_value=10, step=1)
st.write(f"how many cups : {cups}")

name = st.text_input("Enter your name")
if name :
    st.write(f"welcome to chai ghar {name}: your chai is on the wayðŸ’–ðŸ˜‰")

dob = st.date_input("Enter your date")
if dob :
    st.write(f"your birth date: {dob}")

curr_year = 2025
st.write(f"your age: {abs(curr_year - dob.year)}")

col1, col2 = st.columns(2)

with col1:
    st.header("Masala Chai")
    st.image("data:image/jpeg;base64," \
    "", width=200)
    vote1 = st.button("Vote for masala chai") 

with col2:
    st.header("Adrak Chai")
    st.image("data:image/jpeg;base64," \
    "", width=200)
    vote2 = st.button("Vote for Adreak chai")

if vote1:
    st.success("Thanks for voting masala chai")
else:
    st.success("Thanks for voting adrak chai")

name = st.sidebar.text_input("Enter your name : ")
tea = st.sidebar.selectbox("Choose your chai", ["Masala", "Kesar", "Adrak"])
st.sidebar.write(f"welcome {name } and your {tea} chai is getting ready")

with st.expander("Show Chai Making Instructions"):
    st.write("""
    1. Boil water with tea leaves
    2. Add milk and spices 
    3. Serve hot 
    """)

st.markdown('# Welcome to chai app')
st.markdown('> Blackquote')

